<script setup>
import { onBeforeUnmount,ref } from "vue";

const rotate = ref(false);
let timer = null;
// 点击筛子，随机显示一篇文章
function handleClickDice() {
  if (timer) {
    clearTimeout(timer);
    timer = null;
  }
  rotate.value = true;
  timer = setTimeout(() => {
    clearTimeout(timer);
    rotate.value = false;
  }, 500);
  // 请求一篇随机文章
}

onBeforeUnmount(() => {
  clearTimeout(timer);
  timer = null;
})
</script>

